mapper.update:
update `group`
set competition_id = #{competitionId},
    group_name     = #{groupName}
where id = #{id}

mapper.delete:
delete
from group_athlete
where group_id = #{groupId}
  and athlete_id = #{athleteId}

mapper.select:
select *, 0 as 'type'
from group_athlete
where group_id = #{groupId}

mapper.delete:
delete
from group_referee
where group_id = #{groupId}
  and referee_id = #{refereeId}

mapper.select:
select *, 2 as 'type'
from group_referee
where group_id = #{groupId}
