mapper.update:
update referee
set name      = #{name},
    id_number = #{idNumber},
    phone     = #{phone},
    team_id   = #{teamId}
where id = #{id}

update team_doctor
set name      = #{name},
    id_number = #{idNumber},
    phone     = #{phone},
    team_id   = #{teamId}
where id = #{id}

update team_leader
set name      = #{name},
    id_number = #{idNumber},
    phone     = #{phone},
    team_id   = #{teamId}
where id = #{id}

mapper.select:
select *, 0 as 'type'
from athlete
where team_id = #{teamId}

select *, 1 as 'type'
from coach
where team_id = #{teamId}

select *, 2 as 'type'
from referee
where team_id = #{teamId}

select *, 3 as 'type'
from team_doctor
where team_id = #{teamId}

select *, 4 as type
from team_leader
where team_id = #{teamId}

select user_id
from referee
where id = #{refereeId}

select event_id
from athlete_event
where athlete_id = #{athleteId}
